catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    // Modified the if to return if the newValue == oldValue to avoid
    // unecessary trips to the server
    if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
        return;
    }

    // Instantiate the GetCountryData Script Include 
    var getCompanyCountry = new GlideAjax('GetCompanyData');
    // Specify the getCountry method
    getCompanyCountry.addParam('sysparm_name', 'getCountry');
    // Pass the Company sys_id
    getCompanyCountry.addParam('sysparm_companyID', g_form.getValue('company'));
    // Send the request to the server
    getCompanyCountry.getXML(populateCompanyCountryField);


    // When the response is back from the server
    function populateCompanyCountryField(response) {
        // Extract the country from the response, clear any value from the company_country field, 
        // set new value in the company_country field
        var countryFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('company_country');
        g_form.setValue('company_country', countryFromScriptInclude);
    }
}
