catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    // Modified the if to return if the newValue == oldValue to avoid
    // unecessary trips to the server
    if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
        return;
    }

    // Instantiate the GetCountryData Script Include 
    var getCompanyPhone = new GlideAjax('GetCompanyData');
    // Specify the getPhone method
    getCompanyPhone.addParam('sysparm_name', 'getPhone');
    // Pass the Company sys_id
    getCompanyPhone.addParam('sysparm_companyID', g_form.getValue('company'));
    // Send the request to the server
    getCompanyPhone.getXML(populateCompanyPhoneField);


    // When the response is back from the server
    function populateCompanyPhoneField(response) {
        // Extract the phone from the response, clear any value from the company_phone field, 
        // set new value in the company_phone field
        var phoneFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('company_phone');
        g_form.setValue('company_phone', phoneFromScriptInclude);
    }
}
