sysauto_script.condition:
// Only run this Scheduled Job if there are active Incidents over 3 months old
var ga = new GlideAggregate('incident');
ga.addAggregate('COUNT');
ga.addQuery('active', 'true');
ga.addQuery('sys_updated_on', '<', gs.daysAgo(90));
ga.query();
ga.next();
ga.getAggregate('COUNT') !== '0';

sysauto_script.script:
// 1. Create an object to store rows from a table
var gr = new GlideRecord('incident');

// 2. Build query
gr.addQuery('active', '=', 'true');
gr.addQuery('sys_updated_on', '<', gs.daysAgo(90));

// 3. Execute query 
gr.query();

// 4. Process returned records
while (gr.next()) {
    gr.active = false;
    gr.update();
}
